const iconMenu = document.querySelector(".icon-menu");
const menuBody = document.querySelector('.menu');
const closeMenu = document.querySelector('.close-menu');
const menuList = document.querySelector('.menu__list');
const menuItem = document.querySelector('.menu__item');
const menuLink = document.querySelector('.menu__link');

menuBody.addEventListener('click', (e) => {
  if (e.target.closest('.menu__link')) {
    menuBody.classList.remove('active');
  }
});

iconMenu.addEventListener('click', (e) => {
  menuBody.classList.add('active');
});

closeMenu.addEventListener('click', (e) => {
  menuBody.classList.remove('active');
});

//==========================================

const btnLogin = document.querySelector('.header__login');
const listLogin = document.querySelector('.menu__item--hidden.link-acc');

const popupLog = document.querySelector('.popup.popup-login');
const popupCreate = document.querySelector('.popup.popup-create');

const createLink = document.querySelector('.popup-login .popup__account a');
const logLink = document.querySelector('.popup-create .popup__account a');

btnLogin.addEventListener('click', (e) => {
  popupOpen(popupLog);
});

popupLog.addEventListener('click', (e) => {
  if (e.target === popupLog) {
    popupClose(popupLog);
  }
});

createLink.addEventListener('click', (e) => {
  popupClose(popupLog);
  popupOpen(popupCreate);
});

popupCreate.addEventListener('click', (e) => {
  if (e.target === popupCreate) {
    popupClose(popupCreate);
  }
});

logLink.addEventListener('click', (e) => {
  popupClose(popupCreate);
  popupOpen(popupLog);
});

listLogin.addEventListener('click', (e) => {
  popupOpen(popupLog);
});

function popupOpen(p) {
  p.classList.add('popup-active');
}
function popupClose(p) {
  p.classList.remove('popup-active');
}







